// --- Babel register configuration (Development Only) ---
require('@babel/register')({
  presets: ['@babel/preset-react'],
  extensions: ['.js', '.jsx']
});

// Replace ES module imports with CommonJS require statements
const React = require('react');
const { useState, useEffect } = React;
const { useNavigate } = require('react-router-dom');
require('./profile.css'); // Note: In Node, CSS isn't processed, but this line remains per your request.

const Profile = () => {
  // Basic profile states
  const [profilePicture, setProfilePicture] = useState(null);
  const [profilePictureFile, setProfilePictureFile] = useState(null); // Store the actual file
  const [walletAddress, setWalletAddress] = useState('');
  const [country, setCountry] = useState('');
  const [city, setCity] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [isAdult, setIsAdult] = useState(false); // 18+ confirmation
  const [notifyQuestUpdates, setNotifyQuestUpdates] = useState(true);
  const [notifyRewardAlerts, setNotifyRewardAlerts] = useState(true);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Fetch user data from the API
  useEffect(() => {
    async function fetchUserData() {
      try {
        const response = await fetch('http://localhost:5000/api/users');
        if (!response.ok) throw new Error('Failed to fetch user data');
        const data = await response.json();
        // Pick the first user if data is an array, otherwise use the returned object
        const currentUser = Array.isArray(data) && data.length > 0 ? data[0] : data;
        setUser(currentUser);
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    }
    fetchUserData();
  }, []);

  // Pre-fill fields when user data is available
  useEffect(() => {
    if (user) {
      setWalletAddress(user.walletAddress || '');
      setCountry(user.country || '');
      setCity(user.city || '');
      setPhoneNumber(user.phoneNumber || '');
      setIsAdult(user.isAdult || false);
      // Load the stored profile picture if available
      if (user.profilePicture) {
        setProfilePicture(user.profilePicture);
      }
    }
  }, [user]);

  // Basic account actions
  const handleLogout = () => {
    localStorage.removeItem('user');
    alert('Logged out successfully!');
    navigate('/login');
  };

  const handleDeleteAccount = async () => {
    if (
      window.confirm(
        'Are you sure you want to delete your account? This action cannot be undone.'
      )
    ) {
      try {
        const response = await fetch(`http://localhost:5000/api/users/${user._id}`, {
          method: 'DELETE'
        });
        if (response.ok) {
          alert('Account deleted successfully.');
          navigate('/signup');
        } else {
          alert('Failed to delete account.');
        }
      } catch (error) {
        console.error('Error deleting account:', error);
        alert('An error occurred while deleting your account.');
      }
    }
  };

  // File/image handling
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setProfilePictureFile(file); // Save the actual file
      setProfilePicture(URL.createObjectURL(file)); // For preview purposes
    }
  };

  // Wallet input handling
  const handleWalletChange = (event) => setWalletAddress(event.target.value);

  // Save/update profile handler using FormData
  const handleSaveProfile = async () => {
    // Validate mandatory fields
    if (!country || !city) {
      alert('Country and City are required.');
      return;
    }
    if (!isAdult) {
      alert('You must confirm that you are 18+.');
      return;
    }

    // Use FormData to include both text and file data
    const formData = new FormData();
    formData.append('walletAddress', walletAddress);
    formData.append('country', country);
    formData.append('city', city);
    formData.append('phoneNumber', phoneNumber);
    formData.append('isAdult', isAdult);
    formData.append('notifyQuestUpdates', notifyQuestUpdates);
    formData.append('notifyRewardAlerts', notifyRewardAlerts);

    if (profilePictureFile) {
      formData.append('profilePicture', profilePictureFile);
    }

    try {
      // Note: Using user._id here ensures a valid ObjectId is sent
      const response = await fetch(`http://localhost:5000/api/users/${user._id}`, {
        method: 'PUT',
        body: formData, // No need to set Content-Type; browser sets it automatically
      });

      if (response.ok) {
        alert('Profile updated successfully!');
      } else {
        alert('Failed to update profile.');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('An error occurred while updating your profile.');
    }
  };

  // Security & Anti-Fraud: manual account lock
  const handleLockAccount = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/users/${user._id}/lock`, {
        method: 'POST'
      });
      if (response.ok) {
        alert('Account has been locked. Please contact support to unlock.');
      } else {
        alert('Failed to lock account.');
      }
    } catch (error) {
      console.error('Error locking account:', error);
      alert('An error occurred while locking your account.');
    }
  };

  // Placeholder for password reset
  const handleResetPassword = () => {
    alert('Password reset functionality is not implemented yet.');
  };

  // Placeholder for 2FA setup
  const handleSetup2FA = () => {
    alert('2FA setup functionality is not implemented yet.');
  };

  const handleApplyKYC = () => {
    alert('KYC application functionality is not implemented yet.');
  };

  if (loading) return <p>Loading user data...</p>;
  if (!user) return <p>No user data available.</p>;

  return (
    <div className="profile-page">
      <div className="profile-container">
        <span className="close-btn" onClick={() => navigate('/')}>
          <span role="img" aria-label="close">❌</span>
        </span>
        <h1>Profile</h1>
        <p>
          Welcome, <strong>{user.username || user.firstName || 'Unknown User'}</strong>!
        </p>

        <div className="profile-details">
          {/* Profile Picture */}
          <div className="profile-picture">
            <img
              src={profilePicture || user.profilePicture || 'default-profile.png'}
              alt="Profile"
              className="profile-img"
            />
            <input type="file" accept="image/*" onChange={handleImageChange} className="upload-file" />
          </div>

          {/* Basic Profile Information */}
          <div className="user-info">
            <label>Username:</label>
            <p>{user.username || 'Not set'}</p>
          </div>

          <div className="user-info">
            <label>Email:</label>
            <p>
              {user.email || 'No email available'}{' '}
              (<span role="img" aria-label="verified">✅</span>)
            </p>
          </div>

          <div className="user-info">
            <label>Wallet Phantom:</label>
            <input
              type="text"
              value={walletAddress}
              onChange={handleWalletChange}
              placeholder="Enter Phantom Wallet Address"
            />
          </div>

          <div className="user-info">
            <label>18+ Confirmation:</label>
            <input
              type="checkbox"
              checked={isAdult}
              onChange={(e) => setIsAdult(e.target.checked)}
            />{' '}
            I confirm that I am 18+
          </div>

          <div className="user-info">
            <label>Country:</label>
            <input
              type="text"
              value={country}
              onChange={(e) => setCountry(e.target.value)}
              placeholder="Enter your country"
            />
          </div>

          <div className="user-info">
            <label>City:</label>
            <input
              type="text"
              value={city}
              onChange={(e) => setCity(e.target.value)}
              placeholder="Enter your city"
            />
          </div>

          <div className="user-info">
            <label>Phone Number:</label>
            <input
              type="text"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              placeholder="Enter your phone number (optional)"
            />
          </div>

          {/* Notification Preferences */}
          <div className="user-info">
            <label>Notification Preferences:</label>
            <div>
              <input
                type="checkbox"
                checked={notifyQuestUpdates}
                onChange={(e) => setNotifyQuestUpdates(e.target.checked)}
              />{' '}
              Quest Updates
            </div>
            <div>
              <input
                type="checkbox"
                checked={notifyRewardAlerts}
                onChange={(e) => setNotifyRewardAlerts(e.target.checked)}
              />{' '}
              Reward Alerts
            </div>
          </div>

          {/* Actions & Settings */}
          <div className="button-group">
            <button onClick={handleSaveProfile} className="btn">
              Save Profile
            </button>
            <button onClick={handleLogout} className="logout-button btn">
              Logout
            </button>
            <button onClick={handleDeleteAccount} className="delete-button btn" style={{ color: 'red' }}>
              Delete Account
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

module.exports = Profile;
